    private void jBananaMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_jBananaMouseClicked
       if(Engine.banana.qtd>0 && Engine.pablo.getLife()<=80){
           Engine.banana.qtd--;
           jBanana.setText(""+(Engine.banana.qtd));
           Engine.pablo.setLife(Engine.pablo.getLife() + 30);
       }
    }//GEN-LAST:event_jBananaMouseClicked

